sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Instantiate the NeedItUtils class.  
	var niutils = new NeedItUtils();
	var needDateTime = new GlideDateTime(current.u_when_needed);

	//Strip time from u_when_needed value to contain date value only. Time is irrelevant.
	var needDate = needDateTime.getLocalDate();

	//Call the isDatePast and isToday methods. Pass the needDate value (date only).
	var isPast = niutils.isDatePast(needDate);
	var isToday = niutils.isDateToday(needDate);

	// If the isDatePast method returns true, the date is in the past.
	if(isPast == true){
		gs.addErrorMessage("When needed date cannot be in the past.  Your request has not been saved to the database.");
		current.setAbortAction(true);
	}

		// If the isDateToday method returns true, the date is today.
	if(isToday == true){
		gs.addErrorMessage("When needed date cannot be today.  Your request has not been saved to the database.");
		current.setAbortAction(true);
	}

})(current, previous);
