sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function() {
	},

	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date/time then strip time so that value tested contains date only
		var rightnow = new GlideDateTime();
		var today = rightnow.getLocalDate();

		// If the testdate is before rightnow, return true, else return false
		if (strDate.before(today)) {
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function(strDate){
		// Create GlideDateTime objects for the current date/time then strip time so that value tested contains date only
		var rightnow = new GlideDateTime();
		var today = rightnow.getLocalDate();

		// If the testdate is today, return true, else return false
		if (strDate.equals(today)) {
			return true;
		}
		else {
			return false;
		}
	},

	type: 'NeedItUtils'
};
